import '../styles/style.css';
import React from "react";
import Logo from "../images/logo2.svg"
import SearchIcon from '@mui/icons-material/Search';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCartOutlined';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import MenuIcon from '@mui/icons-material/Menu';
import NavBar from './NavBar';

function Header() {
  return (
    <header className="flex flex-row justify-between item-center content-center bg-white px-24">
        <img src={Logo} alt="logo de l'entreprise" />
        <NavBar/> 
        <div className="flex flex-row items-center content-center gap-x-4">
            <div className="flex justify-between">
                <div className="relative flex items-center text-gray-400 rounded-2xl border-2 border-grayCustom focus-within:text-gray-4w00 ">
                    <input
                      type="text"
                      name="search"
                      placeholder="Search"
                      autoComplete="off"
                      aria-label="Search"
                      className="pr-2 pl-10 py-1 rounded-2xl font-amstelvar"
                    />  
                    <SearchIcon className="w-3 h-3 absolute ml-3 pointer" />
                </div>
            </div>
            <ShoppingCartIcon className="text-blueCustom"/>
            <AccountCircleIcon className="text-blueCustom"/>
        </div>
        <MenuIcon className="hidden lg:block"/>
    </header>
  )
}

export default Header;
