import React from "react";
import MuiButton from '@mui/material/Button';

const Button = ({ 
  type = "button", 
  variant = "contained", 
  onClick, 
  children, 
  bgColor = "bg-blueCustom",
  width = "auto" 
}) => {
  return (
    <MuiButton
      className={`
        backgroundColor: bgColor,
        width: width,
        '&:hover': { opacity: 0.8 },
        transition: "0.3s"
      `}
      type={type}
      variant={variant}
      onClick={onClick}
    >
      {children}
    </MuiButton>
  );
};

export default Button;




