import { routes } from "../datas/Routes.js";

function NavBar({ isOpen }) {
  const listItems = routes.map((link) => (
    <li key={link.id} className="md:ml-8 text-xl md:my-0 my-7">
      <a className="hover:text-blueCustom duration-500" href={link.href}>
        {link.title}
      </a>
    </li>
  ));

  return (
    <nav className="md:bg-white sm:">
        <ul
            className={`flex flex-col lg:flex-row items-center justify-center gap-x-8 text-base font-amstelvar md:flex md:items-center md:pb-0 pb-12 md:w-full  absolute md:static  md:z-auto transition-all duration-500 ease-in ${
             isOpen ? "top-20 opacity-100" : "top-[-490px] opacity-0"
        }`} >
            {listItems}
        </ul>
    </nav>

  );
}

export default NavBar;
