import React from "react";
import MuiButton from '@mui/material/Button';

const Button = ({ 
  type = "button", 
  variant = "contained", 
  onClick, 
  children, 
  bgColor = "#1976d2",
  textColor = "#fff", 
  width = "auto" 
}) => {
  return (
    <MuiButton
      sx={{
        backgroundColor: bgColor,
        color: textColor,
        width: width,
        '&:hover': { opacity: 0.8 },
        transition: "0.3s"
      }}
      type={type}
      variant={variant}
      onClick={onClick}
    >
      {children}
    </MuiButton>
  );
};

export default Button;




