import {routes} from '../datas/Routes.js';
function NavBar() {
    const listItems = routes.map(link =>
        <li key={link.id} className="md:ml-8 text-xl md:my-0 my-7">
            <a className="hover:text-blueCustom duration-500" href={link.href}>{link.title}</a>
        </li>
    )
    return (
        <>
        {/*<nav className="hidden lg:flex items-center justify-center">*/}
            <ul className={`flex flex-col lg:flex-row items-center justify-center gap-x-8 text-base font-amstelvar md:flex md:items-center md:pb-0 pb-12 absolute md:static bg-white md:z-auto z-[-1] left-0 w-full md:w-auto md:pl-0 pl-9 transition-all duration-500 ease-in ${open ? 'top-20' : 'top-[-490px]'}`}>
                {listItems}
            </ul>
        {/*</nav>*/}
        </>
    )
}

export default NavBar;
