import '../styles/style.css';
import React from "react";
import Logo from "../images/logo2.svg";
import SearchIcon from '@mui/icons-material/Search';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCartOutlined';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import MenuIcon from '@mui/icons-material/MenuOutlined';
import NavBar from './NavBar';
import Button from "./Button";

function Header() {
  return (
    <header className="flex flex-row justify-between items-center p-4 lg:p-6  bg-white p">
        {/* Logo */}
        <img src={Logo} alt="logo de l'entreprise" className="w-[131px] h-[65px]" />

        {/* Menu de navigation visible uniquement sur les grands écrans */}
        <div className="hidden sm:hidden lg:flex" >
            <NavBar />
        </div>


        {/* Icones de recherche, panier et compte */}
        <div className="hidden lg:flex flex-row items-center gap-x-4">
            {/* Barre de recherche */}
            <div className="relative flex items-center text-gray-400 rounded-2xl border-2 border-grayCustom focus-within:text-gray-400">
                <input
                    type="text"
                    name="search"
                    placeholder="Search"
                    autoComplete="off"
                    aria-label="Search"
                    className="pr-2 pl-10 py-1 rounded-2xl font-amstelvar"
                />
                <SearchIcon className="w-4 h-4 absolute ml-3 pointer-events-none" />
            </div>

            {/* Panier */}
            <ShoppingCartIcon className="text-blueCustom" />

            {/* Compte */}
            <AccountCircleIcon className="text-blueCustom" />
        </div>
        {/* Menu hamburger visible uniquement sur petits écrans */}
        <Button  bgColor={"bg-white"} textColor={"text-black"} padding={"px-2"} className="lg:hidden">
          <MenuIcon className="text-black bold text-4xl"/>
        </Button>

    </header>
  );
}
export default Header;
