import { routes } from '../datas/Routes.js';

function NavBar({ isMenuOpen }) {
    const listItems = routes.map(item =>
        <li key={item.id}>
            <a className="hover:text-blueCustom hover:transition" href={item.href}>{item.title}</a>
        </li>
    );

    return (
        <nav className={`lg:flex ${isMenuOpen ? 'block' : 'hidden'} flex-col lg:flex-row items-center content-center`}>
            <ul className={`flex flex-col lg:flex-row gap-x-8 text-base font-amstelvar ${isMenuOpen ? 'flex' : 'hidden'}`}>
                {listItems}
            </ul>
        </nav>
    );
}

export default NavBar;
