import { routes } from "../datas/Routes.js";

function NavBar({ isOpen }) {
  const listItems = routes.map((link) => (
    <li key={link.id} className="md:ml-8 text-xl md:my-0">
      <a className="hover:text-blueCustom duration-500" href={link.href}>
        {link.title}
      </a>
    </li>
  ));

  return (
      <nav className={`md:w-full sm:w-full bg-white absolute md:static transition-all duration-500 ease-in ${
          isOpen ? "top-25 opacity-100" : "left-0 top-[-490px] opacity-0"
        }`}
      >
          <ul className="flex flex-col lg:flex-row gap-x-4 text-base font-amstelvar  pb-0">
            {listItems}
          </ul>
    </nav>    
  );
}

export default NavBar;
